<script>
    let angka = $state(0)
    let pertanyaan1 = $state(0)
    let nama = $state ("")
    // let pertanyaan2 = $state(0)
    // let pertanyaan3 = $state(0)
    // let pertanyaan4 = $state(0)
    // let pertanyaan5 = $state(0)
    // let total = $state(0)

    let tampilkan = $state();
    function show() {
        tampilkan = 1;
        angka = 6;
        pertanyaan1 = pertanyaan1+1;
        setTimeout(() => {
            tampilkan = 2;
        }, 3000);
    }
    
    // let hasil = () => {
    //     let jumlah = Number(pertanyaan1)+Number(pertanyaan2)+Number(pertanyaan3)+Number(pertanyaan4)+Number(pertanyaan5)
    //     jumlah = total
    // }
    function benar() {
        angka = angka+1;
        pertanyaan1 = pertanyaan1+1;
        };
    function confirm() {
        if (nama.trim() === "") {
            alert ("Harap isi nama terlebih dahulu!");
        }
        else {
            angka = 1
        }
    }

</script>
    

<div class=" text-center ">
    <h1 class=" text-3xl font-bold mt-5">Mari Cek Kepribadianmu!!</h1>
        <h2 class=" text-[100%] font-serif my-5">Mari buktikan apakah kamu anak yang aktif atau introvert</h2>
    </div>
    
    
    {#if angka == 0 }
    <div class=" card bg-base-300 mx-102 py-5 rounded-3xl">
        <div class=" card-body text-center px-12 space-y-2 pt-1">
            <p class="font-semibold text-[16px] ">Sebelum Itu Masukan Nama Kamu Yaa</p>
            <input type="text" bind:value={nama} placeholder="Masukan Nama Kamu" class="input input-bordered input-info w-full max-w-full rounded-2xl" />
            <button class="btn w-full max-w-full bg-indigo-700 font-bold text-[15px] text-white rounded-2xl hover:bg-indigo-800" onclick={confirm}>Mulai</button>
        </div>
        
    </div>

    {:else if angka == 1}
    <div class=" card bg-base-300 mx-132 py-7 rounded-3xl">
        <div class=" card-body text-center py-1 space-y-2">
            <p class=" font-semibold text-[15px] ">Aku merasa nyaman berbicara dengan orang baru.</p>
            <div class=" flex justify-center space-x-4"> 
                <button class="btn bg-green-400 rounded-2xl hover:bg-green-500" onclick={benar} >Ya</button>
                <button class="btn bg-red-400 rounded-2xl hover:bg-red-500" onclick={()=> angka = 2}>Tidak</button>
            </div>
        </div>
    </div>
    {:else if angka == 2}
    <div class=" card bg-base-300 mx-132 py-7 rounded-3xl">
        <div class=" card-body text-center py-1 space-y-2">
            <p class=" font-semibold text-[15px] ">Aku lebih suka berada di tempat ramai daripada sendirian di rumah.</p>
            <div class=" flex justify-center space-x-4">
                <button class="btn bg-green-400 rounded-2xl hover:bg-green-500 " onclick={benar}>Ya</button>
                <button class="btn bg-red-400 rounded-2xl hover:bg-red-500" onclick={()=> angka = 3}>Tidak</button>
            </div>
        </div>
    </div>
    {:else if angka == 3}
    <div class=" card bg-base-300 mx-132 py-7 rounded-3xl">
        <div class=" card-body text-center py-1 space-y-2">
            <p class=" font-semibold text-[15px] ">Aku lebih sering mengajak teman duluan untuk ngobrol atau bermain.</p>
            <div class=" flex justify-center space-x-4">
                <button class="btn bg-green-400 rounded-2xl hover:bg-green-500 " onclick={benar}>Ya</button>
                <button class="btn bg-red-400 rounded-2xl hover:bg-red-500" onclick={()=> angka = 4}>Tidak</button>
            </div>
        </div>
    </div>
    {:else if angka == 4}
    <div class=" card bg-base-300 mx-132 py-7 rounded-3xl">
        <div class=" card-body text-center py-1 space-y-2">
            <p class=" font-semibold text-[15px] ">Aku merasa kelelahan setelah terlalu banyak bersosialisasi.</p>
            <div class=" flex justify-center space-x-4">
                <button class="btn bg-green-400 rounded-2xl hover:bg-green-500" onclick={()=> angka = 5}>Ya</button>
                <button class="btn bg-red-400 rounded-2xl hover:bg-red-500" onclick={benar}>Tidak</button>
            </div>
        </div>
    </div>
    {:else if angka == 5}
    <div class=" card bg-base-300 mx-132 py-7 rounded-3xl">
        <div class=" card-body text-center py-1 space-y-2">
            <p class=" font-semibold text-[15px] ">Aku sering merasa canggung atau tidak nyaman saat berada di tengah banyak orang.</p>
            <div class=" flex justify-center space-x-4">
                <button class="btn bg-green-400 rounded-2xl hover:bg-green-500"onclick={show}>Ya</button>
                <button class="btn bg-red-400 rounded-2xl hover:bg-red-500" onclick={show}>Tidak</button>
            </div>
        </div>
    </div>
    {:else if angka == 6}
    
    {#if tampilkan==1}
    <div class=" text-center  ">

        <p class=" text-[18px] font-bold">Mikirr Sekk!!</p>
        <div class="flex justify-center">
            <img class=" w-72 h-72" src="data:image/jpeg;base64," alt="">
        </div>
    </div>
        {:else if tampilkan==2}
        
        <div class=" card bg-base-300 mx-132 py-7 rounded-3xl">
            <div class=" card-body text-center py-1 space-y-2 font-bold text-[16px]">
        
        {#if pertanyaan1==5}
        <img src="data:image/jpeg;base64," alt="">
        <p>
            Hai {nama}, Kamu adalah anak yang Aktif
        </p>
        {:else if pertanyaan1<=5&&pertanyaan1>1}    
        <img src="data:image/jpeg;base64," alt="">
        <p>
            Hai {nama}, Kamu adalah anak yang Ambivert
        </p>
        {:else if pertanyaan1==1}
        <img src="data:image/jpeg;base64," alt="">
        <p>
            Hai {nama}, Kamu adalah anak yang Introvert
        </p>
        {/if}    
    </div>
</div>
    {/if}
    
{/if}

   